function validateEmail(e){return/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(String(e).toLowerCase())}document.querySelector("#searchBtn").addEventListener("click",function(){let e=document.querySelector("#emailInput").value;""!==e&&validateEmail(e)?(document.querySelector("#emailInput").classList.remove("error"),document.querySelector("#labelInput").style.visibility="visible",document.querySelector("#errorLabel").style.visibility="hidden",window.location.href="/result?search="+e):(document.querySelector("#emailInput").classList.add("error"),document.querySelector("#labelInput").style.visibility="hidden",document.querySelector("#errorLabel").style.visibility="visible")});